import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { getAuth, signInWithEmailAndPassword } from "firebase/auth";
import { app } from "./firebaseConfig"; // Adjust path if needed

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [agreed, setAgreed] = useState([false, false, false]);
  const [error, setError] = useState("");

  const navigate = useNavigate();
  const auth = getAuth(app);

  const handleCheckboxChange = (index) => {
    const updated = [...agreed];
    updated[index] = !updated[index];
    setAgreed(updated);
  };

  const handleLogin = async () => {
    if (!agreed.every(Boolean)) {
      setError("Please agree to all terms.");
      return;
    }

    try {
      await signInWithEmailAndPassword(auth, email, password);
      navigate("/sadq");
    } catch (err) {
      setError("Invalid credentials. Please try again.");
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center px-4">
        <div className="w-full max-w-md bg-white shadow-md rounded-lg p-8 mx-auto">        <h2 className="text-2xl font-bold text-center mb-2">
          Welcome to SADQ â€“ Secure Access Portal
        </h2>
        <p className="text-sm text-center text-gray-600 mb-6">
          Secure login to protect data and ensure smooth clinical operations.
        </p>
        <div className="mb-4">
        <input
          type="email"
          placeholder="Email"
          className="w-full max-w-sm px-4 py-2 border border-gray-300 rounded focus:outline-none focus:ring"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        </div>
        <div className="mb-4">
        <input
          type="password"
          placeholder="Password"
          className="w-full max-w-sm px-4 py-2 border border-gray-300 rounded focus:outline-none focus:ring"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        </div>
        <div className="space-y-4 text-sm text-gray-800 mb-6">
          <label className="flex items-start space-x-3">
            <input
              type="checkbox"
              checked={agreed[0]}
              onChange={() => handleCheckboxChange(0)}
              className="mt-1"
            />
            <span>
              I acknowledge that I am a clinical user authorized to access the SADQ tool.
            </span>
          </label>
          <label className="flex items-start space-x-3">
            <input
              type="checkbox"
              checked={agreed[1]}
              onChange={() => handleCheckboxChange(1)}
              className="mt-1"
            />
            <span>
              I understand that usage may be monitored for operational tracking and improvement purposes.
            </span>
          </label>
          <label className="flex items-start space-x-3">
            <input
              type="checkbox"
              checked={agreed[2]}
              onChange={() => handleCheckboxChange(2)}
              className="mt-1"
            />
            <span>
              I agree not to share access credentials or use the tool for unauthorized purposes.
            </span>
          </label>
        </div>

        {error && <p className="text-red-500 text-sm text-center mb-4">{error}</p>}

        <button
          onClick={handleLogin}
          className="w-full bg-indigo-500 text-white font-semibold py-2 rounded hover:bg-indigo-600 transition"
        >
          Log In
        </button>

        <p className="text-xs text-center mt-4 text-gray-500">
          Questions? Contact{" "}
          <a href="mailto:support@genkimd.com" className="text-blue-600 underline">
            support@genkimd.com
          </a>
        </p>
      </div>
    </div>
  );
}

export default Login;
