import React, { useState } from "react";
import { getAuth, signInWithEmailAndPassword, createUserWithEmailAndPassword } from "firebase/auth";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [agreed, setAgreed] = useState([false, false, false]);
  const [error, setError] = useState("");
  const [isRegistering, setIsRegistering] = useState(false);

  const handleCheckboxChange = (index) => {
    const updated = [...agreed];
    updated[index] = !updated[index];
    setAgreed(updated);
  };

  const auth = getAuth();

  const handleSubmit = async () => {
    setError("");
    if (!email || !password) {
      setError("Please enter email and password.");
      return;
    }

    if (!isRegistering && agreed.includes(false)) {
      setError("Please agree to all terms to continue.");
      return;
    }

    try {
      if (isRegistering) {
        await createUserWithEmailAndPassword(auth, email, password);
      } else {
        await signInWithEmailAndPassword(auth, email, password);
      }
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center px-4">
      <div className="w-full max-w-md bg-white shadow-lg rounded-lg p-8">
        <h2 className="text-2xl font-bold text-center mb-2">
          {isRegistering ? "Create an Account" : "Welcome to SADQ – Secure Access Portal"}
        </h2>

        {!isRegistering && (
          <p className="text-sm text-center text-gray-600 mb-6">
            We've upgraded SADQ to a secure, user-authenticated platform to better protect usage data, support future enhancements, and ensure smooth clinical operations.
          </p>
        )}

        <input
          type="email"
          placeholder="Email"
          className="w-full mb-4 px-4 py-2 border border-gray-300 rounded focus:outline-none focus:ring"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />

        <input
          type="password"
          placeholder="Password"
          className="w-full mb-6 px-4 py-2 border border-gray-300 rounded focus:outline-none focus:ring"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />

        {!isRegistering && (
          <div className="space-y-4 text-sm text-gray-800 mb-6">
            {[
              "I acknowledge that I am a clinical user authorized to access the SADQ tool.",
              "I understand that usage may be monitored for operational tracking and improvement purposes.",
              "I agree not to share access credentials or use the tool for unauthorized purposes.",
            ].map((text, i) => (
              <label key={i} className="flex items-start">
                <input
                  type="checkbox"
                  checked={agreed[i]}
                  onChange={() => handleCheckboxChange(i)}
                  className="mt-1 mr-3"
                />
                <span className="leading-snug">{text}</span>
              </label>
            ))}
          </div>
        )}

        {error && <p className="text-red-500 text-sm mb-3">{error}</p>}

        <button
          onClick={handleSubmit}
          className="w-full bg-indigo-500 text-white font-semibold py-2 rounded hover:bg-indigo-600 transition"
        >
          {isRegistering ? "Register" : "Log In"}
        </button>

        <p className="text-xs text-center mt-4 text-gray-500">
          If you have any issues or questions, contact{" "}
          <a href="mailto:support@genkimd.com" className="text-blue-600 underline">
            support@genkimd.com
          </a>
        </p>

        <p className="text-center mt-4 text-sm">
          {isRegistering ? "Already have an account?" : "New user?"}{" "}
          <button className="text-blue-600 underline" onClick={() => setIsRegistering(!isRegistering)}>
            {isRegistering ? "Log In" : "Register here"}
          </button>
        </p>
      </div>
    </div>
  );
}

export default Login;
