import React, { useState } from "react";
import { getAuth, signInWithEmailAndPassword } from "firebase/auth";
import { useNavigate } from "react-router-dom";
import { app } from "./firebaseConfig";

const auth = getAuth(app);

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [checkboxes, setCheckboxes] = useState([false, false, false]);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const allChecked = checkboxes.every(Boolean);

  const handleCheckboxChange = (index) => {
    const updated = [...checkboxes];
    updated[index] = !updated[index];
    setCheckboxes(updated);
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");

    if (!allChecked) {
      setError("Please agree to all the terms.");
      return;
    }

    try {
      await signInWithEmailAndPassword(auth, email, password);
      navigate("/sad");
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 px-4">
      <div className="bg-white shadow-lg rounded-lg p-8 max-w-xl w-full">
        <h1 className="text-2xl font-bold mb-2 text-center">
          Welcome to SADQ – Secure Access Portal
        </h1>
        <p className="text-gray-700 text-sm text-center mb-6">
          We’ve upgraded SADQ to a secure, user-authenticated platform to better protect usage data,
          support future enhancements, and ensure smooth clinical operations.
        </p>

        <form onSubmit={handleLogin} className="space-y-4">
          <input
            type="email"
            placeholder="Email"
            className="w-full p-2 border rounded-md"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            className="w-full p-2 border rounded-md"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          <div className="space-y-2 text-sm text-gray-800">
            <p>If this is your first time, complete the short registration form to request access.</p>
            <label className="flex items-start gap-2">
              <input type="checkbox" checked={checkboxes[0]} onChange={() => handleCheckboxChange(0)} />
              <span>I acknowledge that I am a clinical user authorized to access the SADQ tool.</span>
            </label>
            <label className="flex items-start gap-2">
              <input type="checkbox" checked={checkboxes[1]} onChange={() => handleCheckboxChange(1)} />
              <span>I understand that usage may be monitored for operational tracking and improvement purposes.</span>
            </label>
            <label className="flex items-start gap-2">
              <input type="checkbox" checked={checkboxes[2]} onChange={() => handleCheckboxChange(2)} />
              <span>I agree not to share access credentials or use the tool for unauthorized purposes.</span>
            </label>
          </div>

          {error && <p className="text-red-600 text-sm">{error}</p>}

          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 disabled:opacity-50"
            disabled={!allChecked}
          >
            Log In
          </button>
        </form>

        <p className="text-sm text-center mt-4">
          If you have any issues or questions, contact:{" "}
          <a href="mailto:support@genkimd.com" className="text-blue-600 underline">
            support@genkimd.com
          </a>
        </p>
      </div>
    </div>
  );
}
