import React, { useState } from 'react';
import { useAuth } from './AuthProvider';
import { useNavigate } from 'react-router-dom';

function Login() {
  const { login } = useAuth();
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [agreed, setAgreed] = useState([false, false, false]);
  const [error, setError] = useState('');

  const handleCheckboxChange = (index) => {
    const updated = [...agreed];
    updated[index] = !updated[index];
    setAgreed(updated);
  };

  const handleLogin = async () => {
    if (!agreed.every(Boolean)) {
      setError('You must agree to all terms.');
      return;
    }

    try {
      await login(email, password);
      navigate('/sad');
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center px-4">
      <div className="w-full max-w-md bg-white shadow-lg rounded-lg p-8">
        <h2 className="text-2xl font-bold text-center mb-2">
          Welcome to SADQ â€“ Secure Access Portal
        </h2>
        <p className="text-sm text-center text-gray-600 mb-6">
          Secure login to protect data and ensure smooth clinical operations.
        </p>

        <input
          type="email"
          placeholder="Email"
          className="w-full mb-4 px-4 py-2 border border-gray-300 rounded"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />

        <input
          type="password"
          placeholder="Password"
          className="w-full mb-6 px-4 py-2 border border-gray-300 rounded"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />

        <div className="space-y-4 text-sm text-gray-800 mb-4">
          {[
            "I acknowledge that I am a clinical user authorized to access the SADQ tool.",
            "I understand that usage may be monitored for operational tracking and improvement purposes.",
            "I agree not to share access credentials or use the tool for unauthorized purposes."
          ].map((text, i) => (
            <label key={i} className="flex items-start">
              <input
                type="checkbox"
                checked={agreed[i]}
                onChange={() => handleCheckboxChange(i)}
                className="mt-1 mr-3"
              />
              <span className="leading-snug">{text}</span>
            </label>
          ))}
        </div>

        {error && <p className="text-red-500 text-sm mb-2">{error}</p>}

        <button
          onClick={handleLogin}
          className="w-full bg-indigo-500 text-white py-2 rounded hover:bg-indigo-600"
        >
          Log In
        </button>

        <p className="text-xs text-center mt-4 text-gray-500">
          Questions? Contact <a href="mailto:support@genkimd.com" className="text-blue-600">support@genkimd.com</a>
        </p>
      </div>
    </div>
  );
}

export default Login;
